(2289, 240, 'three-column-content_0_column-title', 'Empower'),
(2290, 240, '_three-column-content_0_column-title', 'field_54173b76cd57c'),
(2291, 240, 'three-column-content_0_column-content', 'Programmatic+  is an advanced programmatic solution that empowers advertisers to reach the audience segments they desire and helps publishers sell inventory that might not be sold through direct sales efforts. '),
(2292, 240, '_three-column-content_0_column-content', 'field_54173bbecd57d'),
(2293, 240, 'three-column-content_1_column-title', 'Assure'),
(2294, 240, '_three-column-content_1_column-title', 'field_54173b76cd57c'),
(2295, 240, 'three-column-content_1_column-content', 'Through partnerships with quality publishers, Programmatic+ eliminates the risk that an advertising brand will show up on an unfavorable site. Our clients are assured their ads are placed on sites vetted for content and local interest. And publishers are assured that ads served to their site meet Gamut''s stringent quality  standards.'),
(2296, 240, '_three-column-content_1_column-content', 'field_54173bbecd57d'),
(2297, 240, 'three-column-content_2_column-title', 'Maximize'),
(2298, 240, '_three-column-content_2_column-title', 'field_54173b76cd57c'),
(2299, 240, 'three-column-content_2_column-content', 'Whether a site is sending us twenty million impressions a month — or just twenty — we guarantee that each impression is sold at the highest value we can achieve to increase revenue for our publishers.'),
