(4651, 347, 'lower-sub-title2', '<h2>Let our CrossMedia+ team help you access the audiences you seek.</h2>'),
(4652, 347, '_lower-sub-title2', 'field_54184d7a3f175'),
(4653, 347, 'lower-sub-title2-caption', 'National scale. Local impact. Every time.'),
(4654, 347, '_lower-sub-title2-caption', 'field_54184d883f176'),
(4655, 347, 'three-column-content_0_column-title', 'Planning'),
(4656, 347, '_three-column-content_0_column-title', 'field_54173b76cd57c'),
(4657, 347, 'three-column-content_0_column-content', 'We analyze and uncover insights about your target audience beyond demographics, Gamut explores:\r\nPassions and Interests\r\nMedia habits\r\nLifestyle patterns by time of day \r\nInsights that matter.'),
(4658, 347, '_three-column-content_0_column-content', 'field_54173bbecd57d'),
(4659, 347, 'three-column-content_1_column-title', 'Execution'),
(4660, 347, '_three-column-content_1_column-title', 'field_54173b76cd57c'),
(4661, 347, 'three-column-content_1_column-content', 'The CrossMedia+ team aligns the campaign to your KPIs. \r\nWe reveal insights during each campaign to optimize for the best performance. Self-service and full-service reporting is available daily, weekly, and at specific campaign milestones.'),
(4662, 347, '_three-column-content_1_column-content', 'field_54173bbecd57d'),
