(4745, 350, 'three-column-content_0_column-content', 'Programmatic+ is an advanced programmatic solution that empowers advertisers to reach the audience segments they desire and helps publishers sell inventory that might not be sold through direct sales efforts.'),
(4746, 350, '_three-column-content_0_column-content', 'field_54173bbecd57d'),
(4747, 350, 'three-column-content_1_column-title', 'Assure'),
(4748, 350, '_three-column-content_1_column-title', 'field_54173b76cd57c'),
(4749, 350, 'three-column-content_1_column-content', 'Through partnerships with quality publishers, Programmatic+ eliminates the risk that an advertising brand will show up on an unfavorable site. Our clients are assured their ads are placed on sites vetted for content and local interest. And publishers are assured that ads served to their site meet Gamut''s stringent quality standards.'),
(4750, 350, '_three-column-content_1_column-content', 'field_54173bbecd57d'),
